import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';

function App() {
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector((state) => state.cart);   //useSelector sets up a subscription to redux whenever reedux change -> component function
                                                     // re-executes and we will get latest cart

  useEffect(() => {
    fetch('https://react-http-8ec1c-default-rtdb.firebaseio.com/cart.json',    //re-executes whenever our cart changes
         {
          method: 'PUT', body: JSON.stringify(cart),
        });
  },[cart]);

  return (
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
